con:configuration.scriptText:
import groovy.json.JsonSlurper
def slurper = new JsonSlurper()
def resp = slurper.parseText(messageExchange.getResponseContent())
for(def data in resp.values)
{
	if(data.capability == 'Business Entity')
	{
		log.info  data.id.toString()
		context.testCase.testSuite.project.setPropertyValue("permissionId", data.id.toString())
	}
	if(data.capability == 'Land')
	{
		log.info  data.id.toString()
		context.testCase.testSuite.project.setPropertyValue("permissionId1", data.id.toString())
		break
	}
}

