con:config.script:
int len=4
String uuid = java.util.UUID.randomUUID().toString() .replaceAll("-", "");  
random =uuid.substring(0, len) 
context.testCase.testSuite.project.setPropertyValue("random", random);

con:configuration.scriptText:
import groovy.json.JsonSlurper
def slurper = new JsonSlurper()
def resp = slurper.parseText(messageExchange.getResponseContent())

for(def data in resp.value)
{

	if(data.userName == "iallen")
	{
		log.info  data.id.toString()
		context.testCase.testSuite.project.setPropertyValue("customerId", data.id.toString())
		context.testCase.testSuite.project.setPropertyValue("entityId", data.id.toString())
		break
	}
}

import groovy.json.JsonSlurper
def slurper = new JsonSlurper()
def resp = slurper.parseText(messageExchange.getResponseContent())

context.testCase.testSuite.project.setPropertyValue("businessEntityID", resp.businessEntityID.toString())

